import React, { useState } from 'react';
import { canInsertNewOption } from './scripts/genericAllow';

function GenericOptionsAdder({ maxAllowed }) {
    // États pour les champs
    const [genericType, setGenericType] = useState('');
    const [value, setValue] = useState('');

    // Fonction pour gérer la soumission du formulaire
    const handleSubmit = async (e) => {
        e.preventDefault();

        const canInsert = await canInsertNewOption(genericType, maxAllowed);

        if (canInsert) {
            const data = {
                generic_type: genericType,
                value: value
            };

            try {
                const response = await fetch('/wp-json/booker67/v1/options', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify(data)
                });

                const responseData = await response.json();

                if (responseData.message) {
                    alert(responseData.message);
                } else {
                    alert('Erreur lors de l\'ajout de l\'option.');
                }
            } catch (error) {
                alert('Erreur lors de l\'ajout de l\'option.');
            }
        } else {
            alert('Le nombre maximum d\'options pour ce type a été atteint.');
        }
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <label>
                    Type générique:
                    <input
                        type="text"
                        value={genericType}
                        onChange={(e) => setGenericType(e.target.value)}
                    />
                </label>
                <label>
                    Valeur:
                    <input
                        type="text"
                        value={value}
                        onChange={(e) => setValue(e.target.value)}
                    />
                </label>
                <button type="submit">Ajouter</button>
            </form>
        </div>
    );
}

export default GenericOptionsAdder;
