// EditPrestationModal.jsx
import React, { useState } from 'react';

function EditPrestationModal({ isOpen, onClose, prestation, onSave }) {
    const [cost, setCost] = useState(prestation ? prestation.prestation_cost : '');
    const [duration, setDuration] = useState(prestation ? prestation.prestation_duration : '');

    const handleSave = () => {
        if (prestation) {
            onSave({ ...prestation, prestation_cost: cost, prestation_duration: duration });
        }
        onClose();
    };

    if (!isOpen || !prestation) {
        return null;
    }

    return (
        <div style={{ position: 'fixed', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', background: 'white', padding: '20px', borderRadius: '5px', boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)' }}>
            <h3>Modifier la prestation</h3>
            <div>
                <strong>{prestation.prestation_name}</strong>
            </div>
            <div>
                <label>
                    Coût:
                    <input type="text" value={cost} onChange={(e) => setCost(e.target.value)} />
                </label>
            </div>
            <div>
                <label>
                    Durée:
                    <input type="time" value={duration} onChange={(e) => setDuration(e.target.value)} step="60" />
                </label>
            </div>
            <div>
                <button onClick={handleSave}>Sauvegarder</button>
                <button onClick={onClose} style={{ marginLeft: '10px' }}>Annuler</button>
            </div>
        </div>
    );
}


export default EditPrestationModal;
