import React from 'react';
import './css/appointment.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUsers,faHandsHelping,faHourglassHalf, faCheckCircle, faBan, faCalendarTimes, faCheckDouble, faMoneyBillWave } from '@fortawesome/free-solid-svg-icons';



function getStatusIcon(status) {
    const numericStatus = Number(status);
    switch (numericStatus) {
        case 1:
            return faHourglassHalf;
        case 2:
            return faCheckCircle;
        case 0:
            return faBan;
        case 3:
            return faCalendarTimes;
        case 4:
            return faCheckDouble;
        case 6:
            return faMoneyBillWave;
        default:
            return faMoneyBillWave;
    }
}
function Appointment({ rdv, options, practicianInfo, prestationInfo }) {
    const statusIcon = getStatusIcon(rdv.rdv_status);

    let iconClass ='waiting';
    console.log(typeof (rdv.rdv_status))
    console.log(rdv.rdv_status)
    switch (rdv.rdv_status) {
        case 1:
            console.log(rdv.rdv_status)
            iconClass = 'waiting';
            break;
        case 2:
            console.log(rdv.rdv_status)
            iconClass = 'confirmed';
            break;
        case 0:
            console.log(rdv.rdv_status)
            iconClass = 'cancelled';
            break;
        case 3:
            console.log(rdv.rdv_status)
            iconClass = 'postponed';
            break;
        case 4:
            console.log(rdv.rdv_status)
            iconClass = 'completed';
            break;
        case 6:
            console.log(rdv.rdv_status)
            iconClass = 'paid';
            break;
        // ... autres cas
    }
    getStatusIcon(rdv.rdv_status)
    return (
        <div className="appointment-row">
            <div className="appointment-date">{new Date(rdv.rdv_dateTime).toLocaleDateString()}</div>
            <div className="appointment-time">{new Date(rdv.rdv_dateTime).toLocaleTimeString()}</div>
            <div className="user-name">{rdv.user_firstName} {rdv.user_lastName}</div>
            <div className="user-phone">{rdv.user_phone}</div>
            <div className="user-mail">{rdv.user_mail}</div>

            {options.multiplePracticians && (
                <>
                    <div className="practician-name"><FontAwesomeIcon icon={faUsers} /> </div>

                </>
            )}

            {options.multiPrestations && (
                <div className="prestation-name" onClick={() => showPrestationModal(prestationInfo)}>
                    <FontAwesomeIcon icon={faHandsHelping} />
                </div>
            )}
            <div className="appointment-status">

                {statusIcon && <FontAwesomeIcon icon={statusIcon} className={iconClass}/>}
            </div>
        </div>
    );
}

function showPrestationModal(prestationInfo) {
    // Logique pour afficher un modal avec les informations de la prestation
}

export default Appointment;
