import React from 'react';
import './css/appointment.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUsers,faHandsHelping } from '@fortawesome/free-solid-svg-icons';
function Appointment({ rdv, options, practicianInfo, prestationInfo }) {
    return (
        <div className="appointment-row">
            <div className="appointment-date">{new Date(rdv.rdv_dateTime).toLocaleDateString()}</div>
            <div className="appointment-time">{new Date(rdv.rdv_dateTime).toLocaleTimeString()}</div>
            <div className="user-name">{rdv.user_name}</div>
            <div className="user-phone">{rdv.user_phone}</div>

            {options.multiplePracticians && (
                <>
                    <div className="practician-name"><FontAwesomeIcon icon={faUsers} /> </div>

                </>
            )}

            {options.multiPrestations && (
                <div className="prestation-name" onClick={() => showPrestationModal(prestationInfo)}>
                    <FontAwesomeIcon icon={faHandsHelping} />
                </div>
            )}
        </div>
    );
}

function showPrestationModal(prestationInfo) {
    // Logique pour afficher un modal avec les informations de la prestation
}

export default Appointment;
