#!/usr/bin/env node
const path = require('path')
const fs = require('fs-extra')
const prompts = require('prompts')

const install = async () => {
	const targetDir = path.join(process.cwd(), '.')	
	const info = await prompts([{
		type: 'select',
		name: 'framwork',
		message: 'Pick a framework',
		choices: [
			{ title: 'Vue', value: 'vue'},
			{ title: 'Lit', value: 'lit'},
			{ title: 'Svelte', value: 'svelte', disabled: true },
			{ title: 'React', value: 'react', disabled: true },
		]
	}])
	console.log(info)
}

install()