"use client";
import {
  isBrowser,
  nextFrame,
  wrapAsync
} from "./LHKK7Y5F.js";

// src/sleep.ts
var defaultMs = isBrowser ? 150 : 10;
function sleep(ms = defaultMs) {
  return wrapAsync(async () => {
    await nextFrame();
    await new Promise((resolve) => setTimeout(resolve, ms));
    await nextFrame();
  });
}

export {
  sleep
};
